import React from "react";
import { FaFileAlt, FaEnvelopeOpenText, FaClipboardList, FaCrown } from "react-icons/fa";
import { useNavigate } from "react-router-dom";

const Dashboard = () => {
  const navigate = useNavigate();

  const features = [
    { icon: <FaFileAlt size={28} />, title: "AI Resume Generator", path: "/resume-generator" },
    { icon: <FaEnvelopeOpenText size={28} />, title: "AI Cover Letter", path: "/cover-letter-generator" },
    { icon: <FaClipboardList size={28} />, title: "Job Tracker", path: "/job-tracker" },
    { icon: <FaCrown size={28} />, title: "Premium Upgrade", path: "/premium" },
  ];

  return (
    <div className="min-h-screen bg-gray-900 text-white flex flex-col items-center justify-center p-6">
      <h1 className="text-3xl font-bold mb-6">Dashboard</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-6 w-full max-w-2xl">
        {features.map(({ icon, title, path }, index) => (
          <div
            key={index}
            className="flex items-center space-x-4 p-4 bg-gray-800 rounded-lg cursor-pointer hover:bg-gray-700 transition"
            onClick={() => navigate(path)}
          >
            <div className="text-blue-400">{icon}</div>
            <h2 className="text-lg font-semibold">{title}</h2>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
